                                selectedCategories.push(category);
                              } else {
                                const index = selectedCategories.indexOf(category);
                                if (index > -1) {
                                  selectedCategories.splice(index, 1);
                                }
                              }
                              setGameDetails({ ...gameDetails, category: selectedCategories });
                              setIsChanged(true);
