import React, { useEffect, useState } from 'react';
import { useNavigate } from "react-router";
import Cookies from 'js-cookie';
import Swal from 'sweetalert2';
import teamLogo from './1ds.png'; // Import the local image

function AdminDashboard() {
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = Cookies.get('token');
    if (!token) {
      navigate('/login');
      return;
    }

    fetch('http://localhost:5000/user-info', {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    })
    .then(response => response.json())
    .then(data => {
      if (data.role !== 'admin' && data.role !== 'owner') {
        navigate('/');
        return;
      }

      fetch('http://localhost:5000/admin/users', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      })
      .then(response => response.json())
      .then(data => {
        setUsers(data);
      })
      .catch(error => {
        console.error('Error fetching users:', error);
        Swal.fire({
          icon: 'error',
          title: 'Lỗi',
          text: 'Không thể tải danh sách người dùng',
        });
      });
    })
    .catch(error => {
      console.error('Error fetching user info:', error);
      navigate('/');
    });
  }, [navigate]);

  const handleDeleteUser = (userId) => {
    const token = Cookies.get('token');
    Swal.fire({
      title: 'Bạn có chắc chắn?',
      text: "Bạn sẽ không thể hoàn tác hành động này!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Xóa'
    }).then((result) => {
      if (result.isConfirmed) {
        fetch(`http://localhost:5000/admin/users/${userId}`, {
          method: 'DELETE',
          headers: {
            'Authorization': `Bearer ${token}`
          }
        })
        .then(response => {
          if (response.ok) {
            setUsers(users.filter(user => user.id !== userId));
            Swal.fire(
              'Đã xóa!',
              'Người dùng đã được xóa.',
              'success'
            );
          } else {
            Swal.fire(
              'Lỗi!',
              'Không thể xóa người dùng.',
              'error'
            );
          }
        })
        .catch(error => {
          console.error('Error deleting user:', error);
          Swal.fire(
            'Lỗi!',
            'Không thể xóa người dùng.',
            'error'
          );
        });
      }
    });
  };

  return (
    <div style={{ display: 'flex' }}>
      <nav style={{ width: '200px', backgroundColor: '#f8f9fa', padding: '20px' }}>
        <ul style={{ listStyleType: 'none', padding: 0 }}>
          <li style={{ marginBottom: '10px' }}><a href="/profile">Profile</a></li>
          <li style={{ marginBottom: '10px' }}><a href="/settings">Settings</a></li>
          <li style={{ marginBottom: '10px' }}><a href="/logout">Logout</a></li>
        </ul>
      </nav>
      <div style={{ flex: 1 }}>
        <header style={{ backgroundColor: '#eef1f3', display: 'flex', alignItems: 'center', fontFamily: '"Playwrite Hrvatska Lijeva", Arial, sans-serif', fontSize: '40px', margin: '20px 0', justifyContent: 'center' }}>
          <img src={teamLogo} alt="Team Logo" style={{ marginRight: '20px', width: '10%', height: '10%' }} />
          <div style={{ textAlign: 'center', display: 'flex', alignItems: 'center' }}>
            <span>Admin Dashboard</span>
          </div>
          <img src={teamLogo} alt="Team Logo" style={{ marginLeft: '20px', width: '10%', height: '10%' }} />
        </header>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Tên đăng nhập</th>
              <th>Email</th>
              <th>Vai trò</th>
              <th>Hành động</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.username}</td>
                <td>{user.email}</td>
                <td>{user.role}</td>
                <td>
                  <button onClick={() => handleDeleteUser(user.id)}>Xóa</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminDashboard;
