import React, { useEffect, useState } from 'react';
import axios from 'axios';
import teamLogo from './1ds.png'; // Import the local image

function App() {
  const [games, setGames] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/games')
      .then(response => {
        setGames(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const navigateToHome = () => {
    window.location.href = '/';
  };

  return (
    <>
      <header style={{ display: 'flex', alignItems: 'center', fontFamily: '"Playwrite Hrvatska Lijeva", Arial, sans-serif', fontSize: '40px', margin: '20px 0' }}>
        <img src={teamLogo} alt="Team Logo" style={{ marginLeft: '50px', marginRight: '20px', width: '10%', height: '10%', cursor: 'pointer' }} onClick={navigateToHome} />
        <div style={{ flex: 1, textAlign: 'center' }}>
          <span style={{ cursor: 'pointer' }} onClick={navigateToHome}>Death Squad <span style={{ color: 'red' }}>Team</span></span>
        </div>
        <div style={{ width: '20%', height: '10%', display: 'flex', justifyContent: 'flex-end', alignItems: 'center', marginRight: '50px' }}>
          <button style={{ width: '115px', height: '36px', background: '#7347c1', padding: '0', margin: '0', fontSize: '15px', fontWeight: '500', color: '#fff', display: 'inline-block', boxShadow: '0 4px 30px rgba(0, 0, 0, 0.1)', transition: '0.3s ease', border: '5px', borderRadius: '34px', position: 'relative', marginRight: '10px', cursor: 'pointer' }}>
            Register
          </button>
          <button style={{ width: '115px', height: '36px', background: '#0674ec', padding: '0', margin: '0', fontSize: '15px', fontWeight: '500', color: '#fff', display: 'inline-block', boxShadow: '0 4px 30px rgba(0, 0, 0, 0.1)', transition: '0.3s ease', border: '5px solid #0674ec', borderRadius: '34px', position: 'relative', cursor: 'pointer' }}>
            Login
          </button>
        </div>
      </header>
      <div style={{ backgroundColor: '#eef1f3' }}>
        <div className="game-list" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
          {games.map((game, index) => (
            <div key={index} className="game-item" style={{ border: '1px solid #ccc', padding: '10px', margin: '20px', borderRadius: '8px', backgroundColor: '#fff', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', width: '320px', height: '220px', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'space-between', cursor: 'pointer', transition: 'transform 0.3s' }} onMouseEnter={(e) => e.currentTarget.style.transform = 'scale(1.05)'} onMouseLeave={(e) => e.currentTarget.style.transform = 'scale(1)' }>
              <img src={game.image_url} alt={game.name} style={{ width: '300px', height: '180px', objectFit: 'cover', borderRadius: '8px' }} />
              <h2 style={{ margin: '0', transition: 'color 0.3s' }} onMouseEnter={(e) => { e.currentTarget.style.color = '#0674ec'; }} onMouseLeave={(e) => { e.currentTarget.style.color = 'black'; }}>{game.name}</h2>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

export default App;
