import React, { useEffect, useState } from 'react';
import axios from 'axios';
import teamLogo from './1ds.png'; // Import the local image

function App() {
  const [games, setGames] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/games')
      .then(response => {
        setGames(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <>
      <header style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', fontFamily: '"Playwrite Hrvatska Lijeva", Arial, sans-serif', fontSize: '30px', margin: '20px 0' }}>
        <img src={teamLogo} alt="Team Logo" style={{ marginLeft: '10px', marginRight: '20px' }} />
        <div style={{ flex: 1, textAlign: 'center', position: 'relative' }}>
          <div style={{ position: 'absolute', left: '50%', transform: 'translateX(-50%)' }}>
            Death Squad <span style={{ color: 'red' }}>Team</span>
          </div>
        </div>
      </header>
      <div style={{ backgroundColor: '#eef1f3' }}>
        <h1>Game List</h1>
        <div className="game-list">
          {games.map((game, index) => (
            <div key={index} className="game-item">
              <img src={game.image_url} alt={game.name} />
              <h2>{game.name}</h2>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

export default App;
