  const handleSaveGame = () => {
    const updatedGameDetails = {
      ...gameDetails,
      update_date: new Date().toISOString().slice(0, 19).replace('T', ' '), // Add current date in MySQL datetime format
      description: cleanHtmlTags(gameDetails.description), // Clean HTML tags before saving
      minimum_requirements: cleanHtmlTags(gameDetails.minimum_requirements), // Clean HTML tags before saving
      recommended_requirements: cleanHtmlTags(gameDetails.recommended_requirements), // Clean HTML tags before saving
      notes: cleanHtmlTags(gameDetails.notes), // Clean HTML tags before saving
      version: cleanHtmlTags(gameDetails.version) // Clean HTML tags before saving
    };

    const url = selectedGame === 'new' ? 'http://localhost:5000/games/new' : `http://localhost:5000/games/${selectedGame}`;
    const method = selectedGame === 'new' ? 'POST' : 'PUT';

    fetch(url, {
      method: method,
      headers: {
        'Authorization': `Bearer ${Cookies.get('token')}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(updatedGameDetails)
    })
    .then(response => {
      if (response.ok) {
        Swal.fire(
