import React, { useEffect, useState } from 'react';
import axios from 'axios';
import teamLogo from './1ds.png'; // Import the local image
import { useNavigate } from "react-router";
import Cookies from 'js-cookie';

function App() {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [userAvatar, setUserAvatar] = useState(null);
  const [username, setUsername] = useState('');
  const gamesPerPage = 16;
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:5000/games')
      .then(response => {
        setGames(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  useEffect(() => {
    const token = Cookies.get('token');
    if (token) {
      fetch('http://localhost:5000/user-info', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      })
      .then(response => response.json())
      .then(data => {
        setUserAvatar(data.avatar_url);
        setUsername(data.username); // Set the username
      })
      .catch(error => console.error('Error fetching user info:', error));
    }
  }, []);

  useEffect(() => {
    const params = new URLSearchParams();
    params.append('page', currentPage);
    navigate(`?${params.toString()}`);
  }, [currentPage, navigate]);

  const navigateToHome = () => {
    window.location.href = '/';
  };

  const navigateToRegister = () => {
    navigate('/register');
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const filteredGames = games.filter(game =>
    game.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const indexOfLastGame = currentPage * gamesPerPage;
  const indexOfFirstGame = indexOfLastGame - gamesPerPage;
  const currentGames = filteredGames.slice(indexOfFirstGame, indexOfLastGame);

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
    window.scrollTo({ top: 0, behavior: 'smooth' }); // Scroll to top with smooth behavior
  };

  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(filteredGames.length / gamesPerPage); i++) {
    pageNumbers.push(i);
  }

  const renderPageNumbers = () => {
    const totalPages = pageNumbers.length;
    const maxPagesToShow = Math.min(5, totalPages); // Ensure maxPagesToShow does not exceed totalPages
    const halfMaxPagesToShow = Math.floor(maxPagesToShow / 2);
    let startPage = Math.max(1, currentPage - halfMaxPagesToShow);
    let endPage = Math.min(totalPages, currentPage + halfMaxPagesToShow);

    if (currentPage <= halfMaxPagesToShow) {
      endPage = maxPagesToShow;
    } else if (currentPage + halfMaxPagesToShow >= totalPages) {
      startPage = totalPages - maxPagesToShow + 1;
    }

    const pages = [];
    if (startPage > 1) {
      pages.push(1);
      if (startPage > 2) {
        pages.push('...');
      }
    }

    for (let i = startPage; i <= endPage; i++) {
      pages.push(i);
    }

    if (endPage < totalPages) {
      if (endPage < totalPages - 1) {
        pages.push('...');
      }
      pages.push(totalPages);
    }

    return pages;
  };

  return (
    <>
      <header style={{ backgroundColor: '#eef1f3', display: 'flex', alignItems: 'center', fontFamily: '"Playwrite Hrvatska Lijeva", Arial, sans-serif', fontSize: '40px', margin: '20px 0' }}>
        <img src={teamLogo} alt="Team Logo" style={{ marginLeft: '50px', marginRight: '20px', width: '10%', height: '10%', cursor: 'pointer' }} onClick={navigateToHome} />
        <div style={{ flex: 1, textAlign: 'center' }}>
          <span style={{ cursor: 'pointer' }} onClick={navigateToHome}>Death Squad <span style={{ color: 'red' }}>Team</span></span>
        </div>
        <div style={{ width: '20%', height: '10%', display: 'flex', justifyContent: 'flex-end', alignItems: 'center', marginRight: '50px' }}>
          {userAvatar ? (
            <>
              <img src={`http://localhost:5000/${userAvatar}`} alt="User Avatar" style={{ width: '35px', height: '35px', borderRadius: '50%', cursor: 'pointer' }} onClick={() => navigate('/profile')} />
              <span style={{ marginLeft: '10px', fontSize: '14px', fontFamily: 'Quicksand, sans-serif', fontWeight: 'bold' }}>{username}</span> {/* Display the username */}
            </>
          ) : (
            <>
              <button style={{ width: '115px', height: '36px', background: '#7347c1', padding: '0', margin: '0', fontSize: '15px', fontWeight: '500', color: '#fff', display: 'inline-block', boxShadow: '0 4px 30px rgba(0, 0, 0, 0.1)', transition: '0.3s ease', border: '5px solid #7347c1', borderRadius: '34px', position: 'relative', marginRight: '10px', cursor: 'pointer' }} onMouseEnter={(e) => e.currentTarget.style.background = '#5a36a1'} onMouseLeave={(e) => e.currentTarget.style.background = '#7347c1'} onClick={navigateToRegister}>
                Register
              </button>
              <button style={{ width: '115px', height: '36px', background: '#0674ec', padding: '0', margin: '0', fontSize: '15px', fontWeight: '500', color: '#fff', display: 'inline-block', boxShadow: '0 4px 30px rgba(0, 0, 0, 0.1)', transition: '0.3s ease', border: '5px solid #0674ec', borderRadius: '34px', position: 'relative', cursor: 'pointer' }} onMouseEnter={(e) => e.currentTarget.style.background = '#045bb5'} onMouseLeave={(e) => e.currentTarget.style.background = '#0674ec'} onClick={() => navigate('/login')}>
                Login
              </button>
            </>
          )}
        </div>
      </header>
      <style>
        {`
          ::-webkit-scrollbar {
            display: none;
          }
          html {
            -ms-overflow-style: none;  /* IE and Edge */
            scrollbar-width: none;  /* Firefox */
          }
        `}
      </style>
      <div style={{ padding: '20px', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
        <input
          type="text"
          placeholder="🔍 Search games..."
          value={searchTerm}
          onChange={handleSearchChange}
          style={{ width: '30%', padding: '10px', fontSize: '16px', marginBottom: '20px', borderRadius: '8px', border: '1px solid #ccc' }}
        />
      </div>
      <div className="game-list" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
        {currentGames.map((game, index) => (
          <div key={index} className="game-item" style={{ border: '1px solid #ccc', padding: '10px', margin: '20px', borderRadius: '8px', backgroundColor: '#fff', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', width: '320px', height: '220px', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'space-between', cursor: 'pointer', transition: 'transform 0.3s' }} onMouseEnter={(e) => e.currentTarget.style.transform = 'scale(1.05)'} onMouseLeave={(e) => e.currentTarget.style.transform = 'scale(1)' }>
            <img src={game.image_url} alt={game.name} style={{ width: '300px', height: '180px', objectFit: 'cover', borderRadius: '8px' }} />
            <h2 style={{ margin: '0', transition: 'color 0.3s', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis', maxWidth: '280px' }} onMouseEnter={(e) => { e.currentTarget.style.color = '#0674ec'; }} onMouseLeave={(e) => { e.currentTarget.style.color = 'black'; }}>{game.name}</h2>
          </div>
        ))}
      </div>
      <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
        <ul style={{ display: 'flex', listStyle: 'none', padding: '0' }}>
          {renderPageNumbers().map((number, index) => (
            <li key={index} style={{ margin: '0 5px' }}>
              {number === '...' ? (
                <span style={{ padding: '10px 20px' }}>...</span>
              ) : (
                <button onClick={() => paginate(number)} style={{ padding: '10px 20px', cursor: 'pointer', backgroundColor: currentPage === number ? '#0674ec' : '#fff', color: currentPage === number ? '#fff' : '#0674ec', border: '1px solid #0674ec', borderRadius: '5px' }}>
                  {number}
                </button>
              )}
            </li>
          ))}
        </ul>
      </div>
    </>
  );
}

export default App;
