import React, { useEffect, useState } from 'react';
import { useNavigate } from "react-router";
import Cookies from 'js-cookie';
import Swal from 'sweetalert2';
import teamLogo from './1ds.png'; // Import the local image
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'; // Import FontAwesomeIcon
import { faUsers, faGamepad, faFlag } from '@fortawesome/free-solid-svg-icons'; // Import specific icons

function getUsageColor(percentage) {
  if (percentage <= 70) {
    return '#2cff05'; // Green color for 0-50%
  } else if (percentage <= 90) {
    return '#FDFD49';
  } else {
    return 'red';
  }
}

const getRoleColor = (role) => {
  switch (role) {
    case 'owner':
      return '#e74c3c'; // Red for owner
    case 'admin':
      return '#f39c12'; // Orange for admin
    case 'user':
      return '#2ecc71'; // Green for user
    default:
      return 'black'; // Default color
  }
};

const getRoleDisplayOrder = (role) => {
  switch (role) {
    case 'owner':
      return 1;
    case 'admin':
      return 2;
    case 'user':
      return 3;
    default:
      return 4;
  }
};

function AdminDashboard() {
  const [users, setUsers] = useState([]);
  const [cpuUsage, setCpuUsage] = useState(0);
  const [memoryUsage, setMemoryUsage] = useState(0);
  const [diskUsage, setDiskUsage] = useState(0);
  const [cpuInfo, setCpuInfo] = useState({ cores: 0, speed: 0 });
  const [memoryInfo, setMemoryInfo] = useState({ total: 0, used: 0 });
  const [diskInfo, setDiskInfo] = useState({ total: 0, used: 0 });
  const [selectedMenu] = useState('users'); // Remove setSelectedMenu
  const navigate = useNavigate();

  useEffect(() => {
    const token = Cookies.get('token');
    if (!token) {
      navigate('/login');
      return;
    }

    fetch('http://localhost:5000/user-info', {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    })
    .then(response => response.json())
    .then(data => {
      if (data.role !== 'admin' && data.role !== 'owner') {
        navigate('/');
        return;
      }

      fetch('http://localhost:5000/admin/users', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      })
      .then(response => response.json())
      .then(data => {
        setUsers(data);
      })
      .catch(error => {
        console.error('Error fetching users:', error);
        Swal.fire({
          icon: 'error',
          title: 'Lỗi',
          text: 'Không thể tải danh sách người dùng',
        });
      });
    })
    .catch(error => {
      console.error('Error fetching user info:', error);
      navigate('/');
    });

    const ws = new WebSocket('ws://localhost:5000');
    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.type === 'systemUsage') {
        setCpuUsage(data.cpuUsage);
        setMemoryUsage(data.memoryUsage);
        setDiskUsage(data.diskUsage);
        setCpuInfo({ cores: data.cpuCores, speed: data.cpuSpeed });
        setMemoryInfo({ total: Math.round(data.totalMemory), used: Number(data.usedMemory).toFixed(2) });
        setDiskInfo({ total: Math.round(data.totalDisk), used: Number(data.usedDisk).toFixed(2) });
      }
    };

    return () => {
      ws.close();
    };
  }, [navigate]);

  useEffect(() => {
    document.body.style.overflow = 'hidden'; // Disable scrolling for the webpage
    return () => {
      document.body.style.overflow = 'auto'; // Restore scrollbar on component unmount
    };
  }, []);

  useEffect(() => {
    document.body.style.overflow = 'auto'; // Allow scrolling
    document.body.style.scrollbarWidth = 'none'; // Hide scrollbar for Firefox
    document.body.style.msOverflowStyle = 'none'; // Hide scrollbar for IE and Edge

    const style = document.createElement('style');
    style.innerHTML = `
      ::-webkit-scrollbar {
        display: none; /* Hide scrollbar for Chrome, Safari, and Opera */
      }
    `;
    document.head.appendChild(style);

    return () => {
      document.body.style.overflow = 'auto'; // Restore scrollbar on component unmount
      document.head.removeChild(style);
    };
  }, []);

  const handleDeleteUser = (userId) => {
    const token = Cookies.get('token');
    Swal.fire({
      title: 'Bạn có chắc chắn?',
      text: "Bạn sẽ không thể hoàn tác hành động này!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Xóa'
    }).then((result) => {
      if (result.isConfirmed) {
        fetch(`http://localhost:5000/admin/users/${userId}`, {
          method: 'DELETE',
          headers: {
            'Authorization': `Bearer ${token}`
          }
        })
        .then(response => {
          if (response.ok) {
            setUsers(users.filter(user => user.id !== userId));
            Swal.fire(
              'Đã xóa!',
              'Người dùng đã được xóa.',
              'success'
            );
          } else {
            Swal.fire(
              'Lỗi!',
              'Không thể xóa người dùng.',
              'error'
            );
          }
        })
        .catch(error => {
          console.error('Error deleting user:', error);
          Swal.fire(
            'Lỗi!',
            'Không thể xóa người dùng.',
            'error'
          );
        });
      }
    });
  };

  const handleBanUser = (userId) => {
    const token = Cookies.get('token');
    Swal.fire({
      title: 'Bạn có chắc chắn?',
      text: "Bạn muốn cấm người dùng này?",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Cấm'
    }).then((result) => {
      if (result.isConfirmed) {
        fetch(`http://localhost:5000/admin/users/ban/${userId}`, {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`
          }
        })
        .then(response => {
          if (response.ok) {
            setUsers(users.map(user => user.id === userId ? { ...user, banned: true } : user));
            Swal.fire(
              'Đã cấm!',
              'Người dùng đã bị cấm.',
              'success'
            );
          } else {
            Swal.fire(
              'Lỗi!',
              'Không thể cấm người dùng.',
              'error'
            );
          }
        })
        .catch(error => {
          console.error('Error banning user:', error);
          Swal.fire(
            'Lỗi!',
            'Không thể cấm người dùng.',
            'error'
          );
        });
      }
    });
  };

  const handleViewUserInfo = (userId) => {
    const token = Cookies.get('token');
    fetch(`http://localhost:5000/admin/users/${userId}`, {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    })
    .then(response => response.json())
    .then(data => {
      Swal.fire({
        title: 'Thông tin người dùng',
        html: `<p>ID: ${data.id}</p><p>Tên đăng nhập: ${data.username}</p><p>Email: ${data.email}</p><p>Vai trò: ${data.role}</p>`,
        icon: 'info'
      });
    })
    .catch(error => {
      console.error('Error fetching user info:', error);
      Swal.fire({
        icon: 'error',
        title: 'Lỗi',
        text: 'Không thể tải thông tin người dùng',
      });
    });
  };

  const handleEditUserRole = (userId) => {
    const token = Cookies.get('token');
    Swal.fire({
      title: 'Chỉnh sửa vai trò người dùng',
      input: 'select',
      inputOptions: {
        'user': 'User',
        'admin': 'Admin',
        'owner': 'Owner'
      },
      inputPlaceholder: 'Chọn vai trò',
      showCancelButton: true,
      confirmButtonText: 'Lưu',
      cancelButtonText: 'Hủy'
    }).then((result) => {
      if (result.isConfirmed) {
        fetch(`http://localhost:5000/admin/users/role/${userId}`, {
          method: 'PUT',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ role: result.value })
        })
        .then(response => {
          if (response.ok) {
            setUsers(users.map(user => user.id === userId ? { ...user, role: result.value } : user));
            Swal.fire(
              'Thành công!',
              'Vai trò người dùng đã được cập nhật.',
              'success'
            );
          } else {
            Swal.fire(
              'Lỗi!',
              'Không thể cập nhật vai trò người dùng.',
              'error'
            );
          }
        })
        .catch(error => {
          console.error('Error updating user role:', error);
          Swal.fire(
            'Lỗi!',
            'Không thể cập nhật vai trò người dùng.',
            'error'
          );
        });
      }
    });
  };

  return (
    <div style={{ height: '90vh', overflow: 'hidden' }}>
      <header style={{ backgroundColor: '#eef1f3', display: 'flex', alignItems: 'center', fontFamily: '"Playwrite Hrvatska Lijeva", Arial, sans-serif', fontSize: '40px', margin: '20px 0', justifyContent: 'center' }}>
        <img src={teamLogo} alt="Team Logo" style={{ marginRight: '20px', width: '10%', height: '10%' }} />
        <div style={{ textAlign: 'center', display: 'flex', alignItems: 'center' }}>
          <span>Admin Dashboard</span>
        </div>
        <img src={teamLogo} alt="Team Logo" style={{ marginLeft: '20px', width: '10%', height: '10%' }} />
      </header>
      <div style={{ display: 'flex', height: 'calc(100vh - 80px)' }}>
        <nav style={{ width: '200px', backgroundColor: '#f8f9fa', padding: '1px', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'space-between' }}>
          <ul style={{ listStyleType: 'none', padding: 0,marginTop: '0px',marginBottom: '0px', flex: 1, width: '100%', fontSize: '18px' }}>
            <li style={{ marginBottom: '10px', textAlign: 'center', display: 'flex', alignItems: 'center', justifyContent: 'center', width: '100%' }}>
              <a href="/users" style={{ textDecoration: 'none', color: selectedMenu === 'users' ? 'white' : 'black', backgroundColor: selectedMenu === 'users' ? '#1f52db' : 'transparent', display: 'flex', alignItems: 'center', justifyContent: 'center', width: '100%', padding: '10px', transition: 'background-color 0.3s, color 0.3s', borderRadius: '5px' }} onMouseEnter={(e) => { e.currentTarget.style.backgroundColor = '#1f52db'; e.currentTarget.style.color = 'white'; e.currentTarget.querySelector('svg').style.color = 'white'; }} onMouseLeave={(e) => { if (selectedMenu !== 'users') { e.currentTarget.style.backgroundColor = 'transparent'; e.currentTarget.style.color = 'black'; e.currentTarget.querySelector('svg').style.color = 'black'; } }}>
                <FontAwesomeIcon icon={faUsers} style={{ marginRight: '10px', color: selectedMenu === 'users' ? 'white' : 'black' }} />Users
              </a>
            </li>
            <li style={{ marginBottom: '10px', textAlign: 'center', display: 'flex', alignItems: 'center', justifyContent: 'center', width: '100%' }}>
              <a href="/games" style={{ textDecoration: 'none', color: selectedMenu === 'games' ? 'white' : 'black', backgroundColor: selectedMenu === 'games' ? '#1f52db' : 'transparent', display: 'flex', alignItems: 'center', justifyContent: 'center', width: '100%', padding: '10px', transition: 'background-color 0.3s, color 0.3s', borderRadius: '5px' }} onMouseEnter={(e) => { e.currentTarget.style.backgroundColor = '#1f52db'; e.currentTarget.style.color = 'white'; e.currentTarget.querySelector('svg').style.color = 'white'; }} onMouseLeave={(e) => { if (selectedMenu !== 'games') { e.currentTarget.style.backgroundColor = 'transparent'; e.currentTarget.style.color = 'black'; e.currentTarget.querySelector('svg').style.color = 'black'; } }}>
                <FontAwesomeIcon icon={faGamepad} style={{ marginRight: '10px', color: selectedMenu === 'games' ? 'white' : 'black' }} />Games
              </a>
            </li>
            <li style={{ marginBottom: '10px', textAlign: 'center', display: 'flex', alignItems: 'center', justifyContent: 'center', width: '100%' }}>
              <a href="/report" style={{ textDecoration: 'none', color: selectedMenu === 'report' ? 'white' : 'black', backgroundColor: selectedMenu === 'report' ? '#1f52db' : 'transparent', display: 'flex', alignItems: 'center', justifyContent: 'center', width: '100%', padding: '10px', transition: 'background-color 0.3s, color 0.3s', borderRadius: '5px' }} onMouseEnter={(e) => { e.currentTarget.style.backgroundColor = '#1f52db'; e.currentTarget.style.color = 'white'; e.currentTarget.querySelector('svg').style.color = 'white'; }} onMouseLeave={(e) => { if (selectedMenu !== 'report') { e.currentTarget.style.backgroundColor = 'transparent'; e.currentTarget.style.color = 'black'; e.currentTarget.querySelector('svg').style.color = 'black'; } }}>
                <FontAwesomeIcon icon={faFlag} style={{ marginRight: '10px', color: selectedMenu === 'report' ? 'white' : 'black' }} />Report
              </a>
            </li>
          </ul>
          <div style={{ width: '100%', textAlign: 'center', backgroundColor: '#e9ecef', borderRadius: '5px' }}>
            <div style={{ marginBottom: '5px' }}>
              <p style={{ marginBottom: '1px', marginTop: '1px' }}>CPU Usage </p>
              <div style={{ width: '100%', backgroundColor: '#ddd', borderRadius: '5px' }}>
                <div style={{ width: `${cpuUsage}%`, backgroundColor: getUsageColor(cpuUsage), height: '10px', borderRadius: '5px' }}></div>
              </div>
              <p style={{ marginTop: '1px' }}>{cpuUsage}% of {cpuInfo.cores} cores {cpuInfo.speed} MHz</p>
            </div>
            <div style={{ marginBottom: '5px' }}>
              <p style={{ marginBottom: '1px' }}>Memory Usage</p>
              <div style={{ width: '100%', backgroundColor: '#ddd', borderRadius: '5px' }}>
                <div style={{ width: `${memoryUsage}%`, backgroundColor: getUsageColor(memoryUsage), height: '10px', borderRadius: '5px' }}></div>
              </div>
              <p style={{ marginTop: '1px' }}>{memoryUsage}% ({memoryInfo.used}/{memoryInfo.total} GB)</p>
            </div>
            <div>
              <p style={{ marginBottom: '1px' }}>Disk Usage</p>
              <div style={{ width: '100%', backgroundColor: '#ddd', borderRadius: '5px' }}>
                <div style={{ width: `${diskUsage}%`, backgroundColor: getUsageColor(diskUsage), height: '10px', borderRadius: '5px' }}></div>
              </div>
              <p style={{ marginTop: '1px' }}>{diskUsage}% ({diskInfo.used}/{diskInfo.total} GB)</p>
            </div>
          </div>
        </nav>
        <div style={{ flex: 1, padding: '20px', overflowY: 'auto' }}>
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th style={{ border: '1px solid #ddd', padding: '8px', backgroundColor: '#f2f2f2' }}>ID</th>
                <th style={{ border: '1px solid #ddd', padding: '8px', backgroundColor: '#f2f2f2' }}>Tên đăng nhập</th>
                <th style={{ border: '1px solid #ddd', padding: '8px', backgroundColor: '#f2f2f2' }}>Email</th>
                <th style={{ border: '1px solid #ddd', padding: '8px', backgroundColor: '#f2f2f2' }}>Vai trò</th>
                <th style={{ border: '1px solid #ddd', padding: '8px', backgroundColor: '#f2f2f2', width: '400px' }}>Hành động</th>
              </tr>
            </thead>
            <tbody>
              {users
                .sort((a, b) => getRoleDisplayOrder(a.role) - getRoleDisplayOrder(b.role))
                .map(user => (
                <tr key={user.id}>
                  <td style={{ border: '1px solid #ddd', padding: '8px' }}>{user.id}</td>
                  <td style={{ border: '1px solid #ddd', padding: '8px' }}>{user.username}</td>
                  <td style={{ border: '1px solid #ddd', padding: '8px' }}>{user.email}</td>
                  <td style={{ border: '1px solid #ddd', padding: '8px', color: getRoleColor(user.role) }}>{user.role}</td>
                  <td style={{ border: '1px solid #ddd', padding: '8px', display: 'flex', justifyContent: 'space-between' }}>
                    <button onClick={() => handleViewUserInfo(user.id)} style={{ backgroundColor: '#3498db', color: 'white', border: 'none', padding: '5px 20px', borderRadius: '3px', cursor: 'pointer' }}>Xem thông tin</button>
                    <button onClick={() => handleEditUserRole(user.id)} style={{ backgroundColor: '#2ecc71', color: 'white', border: 'none', padding: '5px 20px', borderRadius: '3px', cursor: 'pointer' }}>Chỉnh sửa quyền</button>
                    <button onClick={() => handleBanUser(user.id)} style={{ backgroundColor: '#f39c12', color: 'white', border: 'none', padding: '5px 20px', borderRadius: '3px', cursor: 'pointer' }}>Ban</button>
                    <button onClick={() => handleDeleteUser(user.id)} style={{ backgroundColor: '#d33', color: 'white', border: 'none', padding: '5px 20px', borderRadius: '3px', cursor: 'pointer' }}>Xóa</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
