      setIsChanged(true);
    } catch (error) {
      console.error(`Error adding new link to ${type}:`, error); // Log any errors
    }
  };
  const handleDeleteDownloadLink = (type, index) => {
    try {
      setGameDetails(prevDetails => {
        const updatedLinks = prevDetails[type].filter((_, i) => i !== index);
        return {
          ...prevDetails,
          [type]: updatedLinks
        };
      });
      setIsChanged(true);
    } catch (error) {
      console.error(`Error deleting link from ${type}:`, error); // Log any errors
    }
  };
  const handleAddGame = () => {
    setSelectedGame('new');
    setGameDetails({
      name: '',
      image_url: placeholderImage,
      // Set placeholder image
      description: '',
      minimum_requirements: '',
      recommended_requirements: '',
      latest_download_links: [],
      update_links: [],
      old_download_links: [],
      vietnamese_link: [],
      notes: '',
      version: '',
      release_date: '',
      // Add release_date field
      category: '',
      // Add category field
      update_links_enabled: false,
      old_download_links_enabled: false,
      vietnamese_link_enabled: false
    });
    setInitialGameName('');
    setInitialGameDescription('');
    setIsChanged(false);
  };
  const handleSearchGame = e => {
    setSearchGameTerm(e.target.value);
  };
  const handleCategoryChange = e => {
    const value = e.target.value;
    setSelectedCategories(prevCategories => prevCategories.includes(value) ? prevCategories.filter(category => category !== value) : [...prevCategories, value]);
  };
  const filteredGames = games.filter(game => {
    const matchesSearchTerm = game.name.toLowerCase().includes(searchGameTerm.toLowerCase()) || game.updated_by.toLowerCase().includes(searchGameTerm.toLowerCase());
    const matchesCategory = selectedCategories.length === 0 || selectedCategories.some(category => game.category.includes(category));
    return matchesSearchTerm && matchesCategory;
  });
  const handleViewDetails = reportId => {
    const token = Cookies.get('token');
    fetch(`https://backend.dstcracks.site/reports/${reportId}`, {
      headers: {
        'Authorization': `Bearer ${token}`
