import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './GameDetail.css'; // Import the CSS file
import teamLogo from './1ds.png'; // Import the local image
import Cookies from 'js-cookie';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faSignOutAlt } from '@fortawesome/free-solid-svg-icons';

function GameDetail() {
  const { gameId } = useParams();
  const [game, setGame] = useState(null);
  const [userAvatar, setUserAvatar] = useState(null);
  const [username, setUsername] = useState('');
  const [showDropdown, setShowDropdown] = useState(false);
  const navigate = useNavigate();
  let dropdownTimeout;

  useEffect(() => {
    axios.get(`http://localhost:5000/games/${gameId}`)
      .then(response => {
        setGame(response.data);
      })
      .catch(error => {
        console.error('Error fetching game details:', error);
      });
  }, [gameId]);

  useEffect(() => {
    const token = Cookies.get('token');
    if (token) {
      fetch('http://localhost:5000/user-info', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      })
      .then(response => response.json())
      .then(data => {
        setUserAvatar(data.avatar_url);
        setUsername(data.username); // Set the username
      })
      .catch(error => console.error('Error fetching user info:', error));
    }
  }, []);

  const navigateToHome = () => {
    window.location.href = '/';
  };

  const navigateToRegister = () => {
    navigate('/register');
  };

  const handleMouseEnter = () => {
    clearTimeout(dropdownTimeout);
    setShowDropdown(true);
  };

  const handleMouseLeave = () => {
    dropdownTimeout = setTimeout(() => {
      setShowDropdown(false);
    }, 500); // Delay of 500ms
  };

  const handleAvatarClick = () => {
    setShowDropdown(!showDropdown);
  };

  const handleLogout = () => {
    Cookies.remove('token');
    window.location.reload(); // Reload the page
  };

  if (!game) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <header style={{ backgroundColor: '#eef1f3', display: 'flex', alignItems: 'center', fontFamily: '"Playwrite Hrvatska Lijeva", Arial, sans-serif', fontSize: '40px', margin: '20px 0' }}>
        <img src={teamLogo} alt="Team Logo" style={{ marginLeft: '50px', marginRight: '20px', width: '10%', height: '10%', cursor: 'pointer' }} onClick={navigateToHome} />
        <div style={{ flex: 1, textAlign: 'center' }}>
          <span style={{ cursor: 'pointer' }} onClick={navigateToHome}>Death Squad <span style={{ color: 'red' }}>Team</span></span>
        </div>
        <div style={{ width: '20%', height: '10%', display: 'flex', justifyContent: 'flex-end', alignItems: 'center', marginRight: '50px', position: 'relative' }}>
          <div style={{ display: 'flex', alignItems: 'center', position: 'relative' }} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
            {userAvatar ? (
              <>
                <img src={`http://localhost:5000/${userAvatar}`} alt="User Avatar" style={{ width: '35px', height: '35px', borderRadius: '50%', cursor: 'pointer' }} onClick={handleAvatarClick} />
                <span style={{ marginLeft: '10px', fontSize: '14px', fontFamily: 'Quicksand, sans-serif', fontWeight: 'bold', cursor: 'pointer' }}>{username}</span> {/* Display the username */}
                {showDropdown && (
                  <div style={{ position: 'absolute', top: '50px', right: '0', backgroundColor: '#fff', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', borderRadius: '8px', padding: '10px', zIndex: 1, width: '100px' }}>
                    <button onClick={() => navigate('/profile')} style={{ display: 'block', width: '100%', padding: '10px', textAlign: 'left', background: 'none', border: 'none', cursor: 'pointer', transition: 'background 0.3s, color 0.3s', borderRadius: '8px' }} onMouseEnter={(e) => { e.currentTarget.style.background = '#0674ec'; e.currentTarget.style.color = '#f7ffff'; }} onMouseLeave={(e) => { e.currentTarget.style.background = 'none'; e.currentTarget.style.color = 'inherit'; }}>
                      <FontAwesomeIcon icon={faUser} style={{ marginRight: '8px' }} /> Hồ sơ
                    </button>
                    <button onClick={handleLogout} style={{ display: 'block', width: '100%', padding: '10px', textAlign: 'left', background: 'none', border: 'none', cursor: 'pointer', transition: 'background 0.3s, color 0.3s', borderRadius: '8px' }} onMouseEnter={(e) => { e.currentTarget.style.background = '#0674ec'; e.currentTarget.style.color = '#f7ffff'; }} onMouseLeave={(e) => { e.currentTarget.style.background = 'none'; e.currentTarget.style.color = 'inherit'; }}>
                      <FontAwesomeIcon icon={faSignOutAlt} style={{ marginRight: '8px' }} /> Logout
                    </button>
                  </div>
                )}
              </>
            ) : (
              <>
                <button style={{ width: '115px', height: '36px', background: '#7347c1', padding: '0', margin: '0', fontSize: '15px', fontWeight: '500', color: '#fff', display: 'inline-block', boxShadow: '0 4px 30px rgba(0, 0, 0, 0.1)', transition: '0.3s ease', border: '5px solid #7347c1', borderRadius: '34px', position: 'relative', marginRight: '10px', cursor: 'pointer' }} onMouseEnter={(e) => e.currentTarget.style.background = '#5a36a1'} onMouseLeave={(e) => e.currentTarget.style.background = '#7347c1'} onClick={navigateToRegister}>
                  Register
                </button>
                <button style={{ width: '115px', height: '36px', background: '#0674ec', padding: '0', margin: '0', fontSize: '15px', fontWeight: '500', color: '#fff', display: 'inline-block', boxShadow: '0 4px 30px rgba(0, 0, 0, 0.1)', transition: '0.3s ease', border: '5px solid #0674ec', borderRadius: '34px', position: 'relative', cursor: 'pointer' }} onMouseEnter={(e) => e.currentTarget.style.background = '#045bb5'} onMouseLeave={(e) => e.currentTarget.style.background = '#0674ec'} onClick={() => navigate('/login')}>
                  Login
                </button>
              </>
            )}
          </div>
        </div>
      </header>
      <style>
        {`
          ::-webkit-scrollbar {
            display: none;
          }
          html {
            -ms-overflow-style: none;  /* IE and Edge */
            scrollbar-width: none;  /* Firefox */
          }
        `}
      </style>
      <div className="game-detail-container">
        <img style={{ alignItems: 'center' }}
          src={`http://localhost:5000/${game.image_url}`} 
          alt={game.name} 
          className="game-image" // Add a class for the image
        />
        <div className="game-info gray-text">
          <span>Phát hành: {new Date(game.release_date).toLocaleDateString()}</span>
          <span>Bởi: {game.updated_by}</span>
          <span>Cập nhật: {new Date(game.update_date).toLocaleDateString()}</span>
        </div>
        <h1 className="game-title">{game.name}</h1> {/* Center the game name */}
        <p style={{ textAlign: 'justify', maxWidth: '90%' }} dangerouslySetInnerHTML={{ __html: game.description }}></p> {/* Render multi-line description with justified text */}
        <div className="game-requirements" style={{ maxWidth: '90%' }}>
          <div style={{ width: '45%' }}>
            <h3>Minimum:</h3>
            <p dangerouslySetInnerHTML={{ __html: game.minimum_requirements }}></p>
          </div>
          <div style={{ width: '45%' }}>
            <h3>Recommended:</h3>
            <p dangerouslySetInnerHTML={{ __html: game.recommended_requirements }}></p>
          </div>
        </div>
        <h3 style={{ textAlign: 'center', width: '90%' }}>Link tải {game.name}</h3> {/* Change the text */}
        <p style={{ textAlign: 'center', maxWidth: '90%', fontWeight: 'bold', color: 'red' }} dangerouslySetInnerHTML={{ __html: game.notes }}></p> {/* Render notes with justified text */}
        <ul style={{ listStyleType: 'none', paddingLeft: 0, textAlign: 'center', maxWidth: '90%' }}> {/* Remove bullet points and center the links */}
          {Array.isArray(game.latest_download_links) ? game.latest_download_links.map((link, index) => (
            <li key={index}><a href={link.url} target="_blank" rel="noopener noreferrer" style={{ textDecoration: 'none', fontWeight: 'bold', fontSize: '16px' }}>{link.name}</a></li>
          )) : <li>No links available</li>}
        </ul>
        <br></br>
        <ul style={{ listStyleType: 'none', paddingLeft: 0, textAlign: 'center', maxWidth: '90%' }}> {/* Remove bullet points and center the links */}
          {Array.isArray(game.update_links) ? game.update_links.map((link, index) => (
            <li key={index}><a href={link.url} target="_blank" rel="noopener noreferrer" style={{ textDecoration: 'none', fontWeight: 'bold', fontSize: '16px' }}>{link.name}</a></li>
          )) : <li>No links available</li>}
        </ul>
        <br></br>
        <h3 style={{ textAlign: 'center', maxWidth: '90%'}}>Link tải bản cũ</h3>
        <ul style={{ listStyleType: 'none', paddingLeft: 0, textAlign: 'center', maxWidth: '90%' }}> {/* Remove bullet points and center the links */}
          {Array.isArray(game.old_download_links) ? game.old_download_links.map((link, index) => (
            <li key={index}><a href={link.url} target="_blank" rel="noopener noreferrer" style={{ textDecoration: 'none', fontWeight: 'bold', fontSize: '16px', maxWidth: '90%' }}>{link.name}</a></li>
          )) : <li>No links available</li>}
        </ul>
        <br></br>
        <h3 style={{ textAlign: 'center', maxWidth: '90%'}}>Link việt hóa</h3>
        <ul style={{ listStyleType: 'none', paddingLeft: 0, textAlign: 'center', maxWidth: '90%' }}> {/* Remove bullet points and center the links */}
          {Array.isArray(game.vietnamese_link) ? game.vietnamese_link.map((link, index) => (
            <li key={index}><a href={link.url} target="_blank" rel="noopener noreferrer" style={{ textDecoration: 'none', fontWeight: 'bold', fontSize: '16px' }}>{link.name}</a></li>
          )) : <li>No links available</li>}
        </ul>
      </div>
    </>
  );
}

export default GameDetail;
