import React, { useEffect, useState } from 'react';
import { useNavigate } from "react-router";
import Cookies from 'js-cookie';
import Swal from 'sweetalert2';
import teamLogo from './1ds.png'; // Import the local image
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'; // Import FontAwesomeIcon
import { faUsers, faGamepad, faFlag } from '@fortawesome/free-solid-svg-icons'; // Import specific icons

function getUsageColor(percentage) {
  if (percentage <= 70) {
    return '#2cff05'; // Green color for 0-50%
  } else if (percentage <= 90) {
    return '#FDFD49';
  } else {
    return 'red';
  }
}

function AdminDashboard() {
  const [users, setUsers] = useState([]);
  const [cpuUsage, setCpuUsage] = useState(0);
  const [memoryUsage, setMemoryUsage] = useState(0);
  const [diskUsage, setDiskUsage] = useState(0);
  const [cpuInfo, setCpuInfo] = useState({ cores: 0, speed: 0 });
  const [memoryInfo, setMemoryInfo] = useState({ total: 0, used: 0 });
  const [diskInfo, setDiskInfo] = useState({ total: 0, used: 0 });
  const navigate = useNavigate();

  useEffect(() => {
    const token = Cookies.get('token');
    if (!token) {
      navigate('/login');
      return;
    }

    fetch('http://localhost:5000/user-info', {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    })
    .then(response => response.json())
    .then(data => {
      if (data.role !== 'admin' && data.role !== 'owner') {
        navigate('/');
        return;
      }

      fetch('http://localhost:5000/admin/users', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      })
      .then(response => response.json())
      .then(data => {
        setUsers(data);
      })
      .catch(error => {
        console.error('Error fetching users:', error);
        Swal.fire({
          icon: 'error',
          title: 'Lỗi',
          text: 'Không thể tải danh sách người dùng',
        });
      });
    })
    .catch(error => {
      console.error('Error fetching user info:', error);
      navigate('/');
    });

    const ws = new WebSocket('ws://localhost:5000');
    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.type === 'systemUsage') {
        setCpuUsage(data.cpuUsage);
        setMemoryUsage(data.memoryUsage);
        setDiskUsage(data.diskUsage);
        setCpuInfo({ cores: data.cpuCores, speed: data.cpuSpeed });
        setMemoryInfo({ total: Math.round(data.totalMemory), used: Number(data.usedMemory).toFixed(2) });
        setDiskInfo({ total: Math.round(data.totalDisk), used: Number(data.usedDisk).toFixed(2) });
      }
    };

    return () => {
      ws.close();
    };
  }, [navigate]);

  const handleDeleteUser = (userId) => {
    const token = Cookies.get('token');
    Swal.fire({
      title: 'Bạn có chắc chắn?',
      text: "Bạn sẽ không thể hoàn tác hành động này!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Xóa'
    }).then((result) => {
      if (result.isConfirmed) {
        fetch(`http://localhost:5000/admin/users/${userId}`, {
          method: 'DELETE',
          headers: {
            'Authorization': `Bearer ${token}`
          }
        })
        .then(response => {
          if (response.ok) {
            setUsers(users.filter(user => user.id !== userId));
            Swal.fire(
              'Đã xóa!',
              'Người dùng đã được xóa.',
              'success'
            );
          } else {
            Swal.fire(
              'Lỗi!',
              'Không thể xóa người dùng.',
              'error'
            );
          }
        })
        .catch(error => {
          console.error('Error deleting user:', error);
          Swal.fire(
            'Lỗi!',
            'Không thể xóa người dùng.',
            'error'
          );
        });
      }
    });
  };

  return (
    <div>
      <header style={{ backgroundColor: '#eef1f3', display: 'flex', alignItems: 'center', fontFamily: '"Playwrite Hrvatska Lijeva", Arial, sans-serif', fontSize: '40px', margin: '20px 0', justifyContent: 'center' }}>
        <img src={teamLogo} alt="Team Logo" style={{ marginRight: '20px', width: '10%', height: '10%' }} />
        <div style={{ textAlign: 'center', display: 'flex', alignItems: 'center' }}>
          <span>Admin Dashboard</span>
        </div>
        <img src={teamLogo} alt="Team Logo" style={{ marginLeft: '20px', width: '10%', height: '10%' }} />
      </header>
      <div style={{ display: 'flex', minHeight: 'calc(100vh - 80px)' }}>
        <nav style={{ width: '200px', backgroundColor: '#f8f9fa', padding: '1px', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'space-between' }}>
          <ul style={{ listStyleType: 'none', padding: 0, flex: 1, width: '100%', fontSize: '18px' }}>
            <li style={{ marginBottom: '10px', textAlign: 'center', display: 'flex', alignItems: 'center', justifyContent: 'center', width: '100%' }}>
              <a href="/users" style={{ textDecoration: 'none', color: 'black', display: 'flex', alignItems: 'center', justifyContent: 'center', width: '100%', transition: 'background-color 0.3s, color 0.3s', borderRadius: '5px' }} onMouseEnter={(e) => { e.currentTarget.style.backgroundColor = '#1f52db'; e.currentTarget.style.color = 'white'; e.currentTarget.querySelector('svg').style.color = 'white'; }} onMouseLeave={(e) => { e.currentTarget.style.backgroundColor = '#f8f9fa'; e.currentTarget.style.color = 'black'; e.currentTarget.querySelector('svg').style.color = 'black'; }}>
                <FontAwesomeIcon icon={faUsers} style={{ marginRight: '10px', color: 'black' }} />Users
              </a>
            </li>
            <li style={{ marginBottom: '10px', textAlign: 'center', display: 'flex', alignItems: 'center', justifyContent: 'center', width: '100%' }}>
              <a href="/games" style={{ textDecoration: 'none', color: 'black', display: 'flex', alignItems: 'center', justifyContent: 'center', width: '100%', transition: 'background-color 0.3s, color 0.3s', borderRadius: '5px' }} onMouseEnter={(e) => { e.currentTarget.style.backgroundColor = '#1f52db'; e.currentTarget.style.color = 'white'; e.currentTarget.querySelector('svg').style.color = 'white'; }} onMouseLeave={(e) => { e.currentTarget.style.backgroundColor = '#f8f9fa'; e.currentTarget.style.color = 'black'; e.currentTarget.querySelector('svg').style.color = 'black'; }}>
                <FontAwesomeIcon icon={faGamepad} style={{ marginRight: '10px', color: 'black' }} />Games
              </a>
            </li>
            <li style={{ marginBottom: '0px', marginTop: '0px', textAlign: 'center', display: 'flex', alignItems: 'center', justifyContent: 'center', width: '100%' }}>
              <a href="/report" style={{ textDecoration: 'none', color: 'black', display: 'flex', alignItems: 'center', justifyContent: 'center', width: '100%', transition: 'background-color 0.3s, color 0.3s', borderRadius: '5px' }} onMouseEnter={(e) => { e.currentTarget.style.backgroundColor = '#1f52db'; e.currentTarget.style.color = 'white'; e.currentTarget.querySelector('svg').style.color = 'white'; }} onMouseLeave={(e) => { e.currentTarget.style.backgroundColor = '#f8f9fa'; e.currentTarget.style.color = 'black'; e.currentTarget.querySelector('svg').style.color = 'black'; }}>
                <FontAwesomeIcon icon={faFlag} style={{ marginRight: '10px', color: 'black' }} />Report
              </a>
            </li>
          </ul>
          <div style={{ width: '100%', textAlign: 'center', backgroundColor: '#e9ecef', borderRadius: '5px' }}>
            <div style={{ marginBottom: '5px' }}>
              <p style={{ marginBottom: '1px', marginTop: '1px' }}>CPU Usage </p>
              <div style={{ width: '100%', backgroundColor: '#ddd', borderRadius: '5px' }}>
                <div style={{ width: `${cpuUsage}%`, backgroundColor: getUsageColor(cpuUsage), height: '10px', borderRadius: '5px' }}></div>
              </div>
              <p style={{ marginTop: '1px' }}>{cpuUsage}% of {cpuInfo.cores} cores {cpuInfo.speed} MHz</p>
            </div>
            <div style={{ marginBottom: '5px' }}>
              <p style={{ marginBottom: '1px' }}>Memory Usage</p>
              <div style={{ width: '100%', backgroundColor: '#ddd', borderRadius: '5px' }}>
                <div style={{ width: `${memoryUsage}%`, backgroundColor: getUsageColor(memoryUsage), height: '10px', borderRadius: '5px' }}></div>
              </div>
              <p style={{ marginTop: '1px' }}>{memoryUsage}% ({memoryInfo.used}/{memoryInfo.total} GB)</p>
            </div>
            <div>
              <p style={{ marginBottom: '1px' }}>Disk Usage</p>
              <div style={{ width: '100%', backgroundColor: '#ddd', borderRadius: '5px' }}>
                <div style={{ width: `${diskUsage}%`, backgroundColor: getUsageColor(diskUsage), height: '10px', borderRadius: '5px' }}></div>
              </div>
              <p style={{ marginTop: '1px' }}>{diskUsage}% ({diskInfo.used}/{diskInfo.total} GB)</p>
            </div>
          </div>
        </nav>
        <div style={{ flex: 1 }}>
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Tên đăng nhập</th>
                <th>Email</th>
                <th>Vai trò</th>
                <th>Hành động</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.username}</td>
                  <td>{user.email}</td>
                  <td>{user.role}</td>
                  <td>
                    <button onClick={() => handleDeleteUser(user.id)}>Xóa</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
