import React, { useEffect, useState } from 'react';
import teamLogo from './1ds.png'; // Import the local image
import { useNavigate } from "react-router";
import Cookies from 'js-cookie';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faSignOutAlt } from '@fortawesome/free-solid-svg-icons';

function Profile() {
  const [userAvatar, setUserAvatar] = useState(null);
  const [username, setUsername] = useState('');
  const [createdAt, setCreatedAt] = useState(''); // Add state for created_at
  const [showDropdown, setShowDropdown] = useState(false);
  const navigate = useNavigate();
  let dropdownTimeout;

  useEffect(() => {
    const token = Cookies.get('token');
    if (token) {
      fetch('http://localhost:5000/user-info', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      })
      .then(response => response.json())
      .then(data => {
        setUserAvatar(data.avatar_url);
        setUsername(data.username); // Set the username
        setCreatedAt(data.created_at); // Set the created_at
      })
      .catch(error => console.error('Error fetching user info:', error));
    }
  }, []);

  const navigateToHome = () => {
    window.location.href = '/';
  };

  const handleMouseEnter = () => {
    clearTimeout(dropdownTimeout);
    setShowDropdown(true);
  };

  const handleMouseLeave = () => {
    dropdownTimeout = setTimeout(() => {
      setShowDropdown(false);
    }, 300); // Delay of 300ms
  };

  const handleDropdownMouseEnter = () => {
    clearTimeout(dropdownTimeout);
    setShowDropdown(true);
  };

  const handleDropdownMouseLeave = () => {
    setShowDropdown(false);
  };

  const handleAvatarClick = () => {
    setShowDropdown(!showDropdown);
  };

  const handleLogout = () => {
    Cookies.remove('token');
    window.location.reload(); // Reload the page
  };

  return (
    <>
      <header style={{ backgroundColor: '#eef1f3', display: 'flex', alignItems: 'center', fontFamily: '"Playwrite Hrvatska Lijeva", Arial, sans-serif', fontSize: '40px', margin: '20px 0' }}>
        <img src={teamLogo} alt="Team Logo" style={{ marginLeft: '50px', marginRight: '20px', width: '10%', height: '10%', cursor: 'pointer' }} onClick={navigateToHome} />
        <div style={{ flex: 1, textAlign: 'center' }}>
          <span style={{ cursor: 'pointer' }} onClick={navigateToHome}>Death Squad <span style={{ color: 'red' }}>Team</span></span>
        </div>
        <div style={{ width: '20%', height: '10%', display: 'flex', justifyContent: 'flex-end', alignItems: 'center', marginRight: '50px', position: 'relative' }} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
          {userAvatar ? (
            <>
              <img src={`http://localhost:5000/${userAvatar}`} alt="User Avatar" style={{ width: '35px', height: '35px', borderRadius: '50%', cursor: 'pointer' }} onClick={handleAvatarClick} />
              <span style={{ marginLeft: '10px', fontSize: '14px', fontFamily: 'Quicksand, sans-serif', fontWeight: 'bold' }}>{username}</span> {/* Display the username */}
              {showDropdown && (
                <div style={{ position: 'absolute', top: '50px', right: '0', backgroundColor: '#fff', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', borderRadius: '8px', padding: '10px', zIndex: 1 }} onMouseEnter={handleDropdownMouseEnter} onMouseLeave={handleDropdownMouseLeave}>
                  <button onClick={() => navigate('/profile')} style={{ display: 'block', width: '100%', padding: '10px', textAlign: 'left', background: 'none', border: 'none', cursor: 'pointer', transition: 'background 0.3s, color 0.3s', borderRadius: '8px' }} onMouseEnter={(e) => { e.currentTarget.style.background = '#0674ec'; e.currentTarget.style.color = '#f7ffff'; }} onMouseLeave={(e) => { e.currentTarget.style.background = 'none'; e.currentTarget.style.color = 'inherit'; }}>
                    <FontAwesomeIcon icon={faUser} style={{ marginRight: '8px' }} /> Hồ sơ
                  </button>
                  <button onClick={handleLogout} style={{ display: 'block', width: '100%', padding: '10px', textAlign: 'left', background: 'none', border: 'none', cursor: 'pointer', transition: 'background 0.3s, color 0.3s', borderRadius: '8px' }} onMouseEnter={(e) => { e.currentTarget.style.background = '#0674ec'; e.currentTarget.style.color = '#f7ffff'; }} onMouseLeave={(e) => { e.currentTarget.style.background = 'none'; e.currentTarget.style.color = 'inherit'; }}>
                    <FontAwesomeIcon icon={faSignOutAlt} style={{ marginRight: '8px' }} /> Logout
                  </button>
                </div>
              )}
            </>
          ) : (
            <>
              <button style={{ width: '115px', height: '36px', background: '#7347c1', padding: '0', margin: '0', fontSize: '15px', fontWeight: '500', color: '#fff', display: 'inline-block', boxShadow: '0 4px 30px rgba(0, 0, 0, 0.1)', transition: '0.3s ease', border: '5px solid #7347c1', borderRadius: '34px', position: 'relative', marginRight: '10px', cursor: 'pointer' }} onMouseEnter={(e) => e.currentTarget.style.background = '#5a36a1'} onMouseLeave={(e) => e.currentTarget.style.background = '#7347c1'} onClick={() => navigate('/register')}>
                Register
              </button>
              <button style={{ width: '115px', height: '36px', background: '#0674ec', padding: '0', margin: '0', fontSize: '15px', fontWeight: '500', color: '#fff', display: 'inline-block', boxShadow: '0 4px 30px rgba(0, 0, 0, 0.1)', transition: '0.3s ease', border: '5px solid #0674ec', borderRadius: '34px', position: 'relative', cursor: 'pointer' }} onMouseEnter={(e) => e.currentTarget.style.background = '#045bb5'} onMouseLeave={(e) => e.currentTarget.style.background = '#0674ec'} onClick={() => navigate('/login')}>
                Login
              </button>
            </>
          )}
        </div>
      </header>
      <style>
        {`
          ::-webkit-scrollbar {
            display: none;
          }
          html {
            -ms-overflow-style: none;  /* IE and Edge */
            scrollbar-width: none;  /* Firefox */
          }
        `}
      </style>
      <section className="author-profile-area">
        <div className="container">
          <div className="row">
            <div className="col-lg-4">
              <aside className="sidebar sidebar_author">
                <div className="author-card sidebar-card">
                  <div className="author-infos">
                    <div className="author_avatar">
                      <img src={`http://localhost:5000/${userAvatar}`} alt="User Avatar" style={{ width: '160px', height: '160px' }} />
                    </div>
                    <div className="author">
                      <h4>{username}</h4>
                      <p>Tham gia: {new Date(createdAt).toLocaleDateString('vi-VN')}</p> {/* Format the created_at date */}
                    </div>
                    <div className="author-badges">
                      <ul className="list-unstyled">
                        <li>
                          <span data-toggle="tooltip" data-placement="bottom" title="Thành viên">
                            <img src="/Assets/Imgs/svg/member.svg" alt="" className="svg" />
                          </span>
                        </li>
                      </ul>
                    </div>
                    <div className="social social--color--filled" style={{ fontSize: '25px', marginBottom: '0' }}>
                      <span data-toggle="tooltip" data-placement="bottom" title="Tình trạng tài khoản: Tốt">
                        <i className="fas fa-user" style={{ color: 'var(--success)' }}></i>
                      </span>
                    </div>
                  </div>
                </div>
              </aside>
            </div>
            <div className="col-lg-8 col-md-12">
              <div className="row">
                <div className="col-md-12 col-sm-12">
                  <div className="author_module about_author">
                    <h2>
                      Thông tin tài khoản
                      <span>{username}</span>
                    </h2>
                    <p><span className="indam">Họ tên:</span> Nguyễn phúc</p>
                    <p><span className="indam">Tên đăng nhập:</span> {username}</p>
                    <p><span className="indam">Email:</span> trantoan101203@gmail.com</p>
                    <p><span className="indam">Điểm LND:</span> 0 LND</p>
                    <p><span className="indam">Điểm VIP:</span> 0</p>
                    <p><span className="indam">SteamID:</span> Chưa có</p>
                    <p><span className="indam">Bài đăng diễn đàn:</span> <a href="/forums/my-post?page=1&amp;tacgiaId=1233963" target="_blank">5 bài đăng</a></p>
                  </div>
                </div>
                <div className="col-md-12 col-sm-12">
                  <div className="author_module about_author">
                    <h2>
                      Cấu hình máy cá nhân
                    </h2>
                    <p>Bạn chưa tải lên cấu hình cá nhân của bạn. Bạn hãy vào phần cài đặt để cập nhật nhé</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}

export default Profile;
