import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function GameDetail() {
  const { gameId } = useParams();
  const [game, setGame] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:5000/games/${gameId}`)
      .then(response => {
        setGame(response.data);
      })
      .catch(error => {
        console.error('Error fetching game details:', error);
      });
  }, [gameId]);

  const gameDetailStyle = {
    padding: '20px',
    overflowY: 'scroll',
    scrollbarWidth: 'none', // For Firefox
    msOverflowStyle: 'none' // For Internet Explorer and Edge
  };

  const gameDetailContainerStyle = {
    ...gameDetailStyle,
    '::-webkit-scrollbar': {
      display: 'none' // For Chrome, Safari, and Opera
    }
  };

  // Apply the styles using a className
  const gameDetailContainerClass = {
    ...gameDetailContainerStyle,
    '&::-webkit-scrollbar': {
      display: 'none' // For Chrome, Safari, and Opera
    }
  };

  if (!game) {
    return <div>Loading...</div>;
  }

  return (
    <div style={gameDetailContainerClass}>
      <h1>{game.name}</h1>
      <img src={`http://localhost:5000/${game.image_url}`} alt={game.name} style={{ width: '100%', height: 'auto' }} />
      <p>{game.description}</p>
      <h3>Cấu hình tối thiểu</h3>
      <p>{game.minimum_requirements}</p>
      <h3>Cấu hình khuyên dùng</h3>
      <p>{game.recommended_requirements}</p>
      <p>Người update: {game.updated_by}</p>
      <p>Ngày phát hành: {new Date(game.release_date).toLocaleDateString()}</p>
      <p>Ngày cập nhật: {new Date(game.update_date).toLocaleDateString()}</p>
      <h3>Link tải bản mới nhất</h3>
      <ul>
        {Array.isArray(game.latest_download_links) ? game.latest_download_links.map((link, index) => (
          <li key={index}><a href={link.url}>{link.name}</a></li>
        )) : <li>No links available</li>}
      </ul>
      <h3>Link update</h3>
      <ul>
        {Array.isArray(game.update_links) ? game.update_links.map((link, index) => (
          <li key={index}><a href={link.url}>{link.name}</a></li>
        )) : <li>No links available</li>}
      </ul>
      <h3>Link tải bản cũ</h3>
      <ul>
        {Array.isArray(game.old_download_links) ? game.old_download_links.map((link, index) => (
          <li key={index}><a href={link.url}>{link.name}</a></li>
        )) : <li>No links available</li>}
      </ul>
      <h3>Link việt hóa</h3>
      <ul>
        {Array.isArray(game.vietnamese_link) ? game.vietnamese_link.map((link, index) => (
          <li key={index}><a href={link.url}>{link.name}</a></li>
        )) : <li>No links available</li>}
      </ul>
    </div>
  );
}

export default GameDetail;
